// 1. Read trigger payload from Webhook node (authoritative time context)
const webhookBody = $items('Webhook')[0].json.body;

if (!webhookBody?.symbol || !webhookBody?.candleBucket) {
  throw new Error('Webhook payload missing symbol or candleBucket');
}

// 2. Read agent output (current item)
const agentItem = $json;

let decision;
try {
  decision =
    typeof agentItem.output === 'string'
      ? JSON.parse(agentItem.output)
      : agentItem;
} catch (e) {
  throw new Error('Agent output is not valid JSON');
}

// 3. Assemble final payload
return [
  {
    symbol: webhookBody.symbol,
    candleBucket: webhookBody.candleBucket,

    action: decision.action,
    confidence: decision.confidence,
    reason: decision.reason || 'No explicit reasoning provided by agent',
  },
];

const input = $input.all();
if (input.length !== 2) {
  throw new Error(`Expected 2 inputs (candles + indicators), got ${input.length}`);
}

/* ---------- Candles (HTTP Request wraps in .body) ---------- */
const candlesResponse = input[0].json;
const candles = candlesResponse?.body?.candles;

if (!Array.isArray(candles)) {
  throw new Error('Candle history missing candles array');
}

/* ---------- Indicators (direct JSON) ---------- */
const indicatorsResponse = input[1].json;
const indicators = indicatorsResponse?.indicators;

if (!Array.isArray(indicators)) {
  throw new Error('Indicators response missing indicators array');
}

/* ---------- Validate window alignment ---------- */
if (candles.length !== indicators.length) {
  throw new Error(
    `Candle / indicator window mismatch: ${candles.length} candles vs ${indicators.length} indicators`
  );
}

if (candles.length === 0) {
  throw new Error('Empty candle window');
}

/* ---------- Extract EMA arrays (index-aligned) ---------- */
const ema9 = indicators.map(i => i.ema_9);
const ema21 = indicators.map(i => i.ema_21);

/* ---------- Final agent-safe payload ---------- */
return [
  {
    candles,
    indicators: {
      ema_9: ema9,
      ema_21: ema21,
    },
  },
];
